        console.log(result);
    
}*/

  


export default function Home(props){
    const [loading, setLoading] = useState(false);
    const [posts, setPosts] = useState([]);
  
    useEffect(() => {
        const loadPost = async () => {
  
            // Till the data is fetch using API 
            // the Loading page will show.
            setLoading(true);
  
            // Await make wait until that 
            // promise settles and return its result
            const response = await axios.get(
            "http://localhost:8085/api/v1/movies?page=1&limit=100");
  
            // After fetching data stored it in posts state.
            setPosts(response.data);
            // Closed the loading page
            setLoading(false);
        }
  
        // Call the function
        loadPost();
    }, []);

    /*const[imageUrls, setImageUrls] = useState([]);

    

    async function getImages(){
    const rawResponse = await fetch('http://localhost:8085/api/v1/movies?page=1&limit=100',{
        method: 'GET'
    });
    if(rawResponse.ok){
        const moviesObject = await rawResponse.json();
        const movies = await moviesObject.movies;
        var urls = await movies.map( movie =>{ 
          return(movie.poster_url);
        });
        return(urls);
        //setImageUrls(urls);
    }
}*/
    
    return(
        <div className="home">
            <div className="header">    
                <Header/>
            </div>
            <div className="heading">
                Upcoming Movies
            </div>
            
            <div id="upcmomingMoviesGrid" className="upcoming-movies">
                <UpcomingGrid imageUrls={posts} />
            </div> 
        </div>
    )
}
