import React from "react";
import Header from "../../common/header/Header";
import "./Home.css";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
import StarBorderIcon from '@material-ui/icons/StarBorder';
import UpcomingGrid from './UpcomingGrid';
import  { useState, useEffect } from 'react';
import ImageList from '@material-ui/core/ImageList'
import axios from 'axios';
import { Paper } from "@material-ui/core";
import ImageListItem from '@material-ui/core/ImageListItem';
import ImageListItemBar from '@material-ui/core/ImageListItemBar';



export default function Home(props){
    
    const[imageUrls, setImageUrls] = useState([]);
    const[titles, setTitles] = useState([]);
    const[movieData, setMovieData] = useState([]);

  
    useEffect(() => {
        const getImageurls = async () => {
  
            const response = await axios.get(
            "http://localhost:8085/api/v1/movies?page=1&limit=100");
                
            const urls = response.data.movies.map((movie)=>{
                return movie.poster_url;
            });
            const titlesT = response.data.movies.map((movie)=>{
                return movie.title;
            });
            console.log(response.data);
           
                setImageUrls(urls);
                setTitles(titlesT);
                setMovieData(response.data);

        }
        getImageurls();
       
    }, []);

   
    
    return(
        <div className="home">
            <div className="header">    
                <Header/>
            </div>
            <div className="heading">
                Upcoming Movies
            </div>
            
            <div id="upcmomingMoviesGrid" className="upcoming-movies">
                <UpcomingGrid imageUrls ={imageUrls} titles = {titles} movieData={movieData}/>
            </div> 
            <div style={{  display: 'flex' }}>  
  
                <div style={{ width: "76%" , margin: "16px"}}>  
                    <Grid >
                        <ImageList rowHeight={250}  cols={4}
                        style ={{ display: 'flex' }}>
                            {imageUrls.map((item,key) => (
                                <ImageListItem key={key+499}>
                                    <img
                                    src={item}
                                    alt="Image not available"
                                    key={key+199}
                                    />
                                    <ImageListItemBar 
                                    title={titles[key]}
                                    subtitle = {sev-puri}
                                    key = {key+99}
                                    />
                                </ImageListItem>
                            ))}
                        </ImageList>     
                    </Grid> 
                </div>  

                <div style={{ width:"24%", margin: "16px"}}>  
                This is the second division to have a vertical alignment.  
                </div>  
            </div>  
        </div>
    )
}
