import React, { useState , useEffect} from "react";
import Header from "../../common/header/Header";
import axios from "axios";

export default function Details(props){
    const[bookShowButton, setBookShowButton] = useState(true);
    const[openedMovieId , SetOpenedMovieId] = useState("");
    

    useEffect(() => {
        
      const  idfunc = async() => {
            
            //console.log(openedMovieId);
            SetOpenedMovieId((props.match.params.id).substring(1));
            const idResponse = await axios.get(`ttp://localhost:8085/api/v1/movies/${openedMovieId}`);
            if(idResponse.ok){
                const movieIdtemp = await idResponse.json();
                console.log(movieIdtemp);
            }

      }  
      idfunc();
      
    }, [])
    

    return( 
        <div className="home">
            <div className="header">    
                <Header props ={props} bookShowButton = {bookShowButton} setBookShowButton = {setBookShowButton}/>
            </div>
            <div className="details-main">

            </div>
        </div> 
    );
}
