export default function Home(props){

    const[imageUrls, setImageUrls] = useState([]);
    const[imageUrl, setImageUrl] = useState([]);
    

    async function getImages(){
    const rawResponse = await fetch('http://localhost:8085/api/v1/movies?page=1&limit=100',{
        method: 'GET'
    });
    if(rawResponse.ok){
        const moviesObject = await rawResponse.json();
        const movies = await moviesObject.movies;
        movies.forEach( (movie) =>{ 
            setImageUrl(movie.poster_url);
            setImageUrls([...imageUrl,imageUrl]);
        });
        console.log(imageUrls);
        //setImageUrls(urls);
        
    }
}
    getImages();
    return(
        <div className="home">
            <div className="header">    
                <Header/>
            </div>
            <div className="heading">
                Upcoming Movies
            </div>
            
            <div id="upcmomingMoviesGrid" className="upcoming-movies">
                <UpcomingGrid imageUrls={imageUrls} />
            </div> 
        </div>
    )
}
