import React, { useState , useEffect} from "react";
import Header from "../../common/header/Header";
import axios from "axios";
import { Typography } from "@material-ui/core";
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import Icon from '@material-ui/core/Icon';
export default function Details(props){
    const[bookShowButton, setBookShowButton] = useState(true);
    const[openedMovie , SetOpenedMovie] = useState("");
    

    useEffect(() => {
        
      const  idfunc = async() => {
            
            //console.log(openedMovieId);
            const idResponse = await fetch(`http://localhost:8085/api/v1/movies/${props.match.params.id}`);
            if(idResponse.ok){
                const movietemp = await idResponse.json();
                SetOpenedMovie(movietemp);
            }

      }  
      idfunc();
      
    }, [])

console.log(openedMovie);
    return( 
        <div className="home">
            <div className="header">    
                 <Header props ={props} bookShowButton = {bookShowButton} setBookShowButton = {setBookShowButton} openedMovie = {openedMovie}/>
            </div>
            <div className="details-main" style={{  display: 'flex' }}>
                <div style={{ width: "20%"  }}>
                    <Typography style={{ cursor: "pointer"}}><Icon fontSize="small" >&lt;Back to Home</Icon></Typography>
                </div>
                <div style={{ width: "60%" }}>
                   
                </div>
                <div style={{ width: "20%"}}>
                    
                </div>
            </div>
        </div> 
    );
}
