import BookShow from "../screens/bookshow/BookShow";
import Confirmation from "../screens/confirmation/Confirmation";
import { useState, useEffect } from "react";




const Controller = () => {
  const baseUrl = "/api/v1/";

  const [nameValue, setNameValue] = useState("ssssss");
  const [emailValue, setEmailValue] = useState("ss@gmail.com");
  const [lastNameValue, setLasNameVale] = useState("sdsd");
  const [contactValue, setContactValue] = useState("8458484");
  const [passwrodValue, setPasswordValue] = useState("asdasad");


   const[registered, setregistered] = useState(false);
   const[registerClick, setregisterClick] = useState(false);



