import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
import StarBorderIcon from '@material-ui/icons/StarBorder';
import Box from '@material-ui/core/Box';
import Paper from '@material-ui/core/Paper';
import ImageListItem from '@material-ui/core/ImageListItem';
import { ImageList } from '@material-ui/core';
import  { useState } from 'react';
import imgX from '../../assets/no-image-available-vector-hand-600w-745639717.webp';




export default function UpcomingGrid() {

    const[imageUrls, setImageUrls] = useState([]);
    const[key,setKey] = useState(1);
    
    async function getImages(){
        const rawResponse = await fetch('http://localhost:8085/api/v1/movies?page=1&limit=100',{
            method: 'GET'
        });
        if(rawResponse.ok){
            const moviesObject = await rawResponse.json();
            const movies = await moviesObject.movies;
            let urls =await movies.map(movie => {
                return (movie.poster_url);
            });
            setImageUrls(urls);
            
        }
    }

    getImages();
   

    return (
       
            <GridList item sx={2}  >
                <ImageList cols={17} >
                    {imageUrls.map((item) => (
                        <ImageListItem >
                            <img
                            src={item}
                            key={key}
                            alt={imgX}
                            />
                            {setKey(key+1)}
                        </ImageListItem>
                    ))}
                </ImageList>
            </GridList>            
      );
    }
