import React from "react";
import Header from "../../common/header/Header";
import "./Home.css";
import PropTypes from 'prop-types';
import { makeStyles } from "@material-ui/styles";
import Grid from '@material-ui/core/Grid';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
import StarBorderIcon from '@material-ui/icons/StarBorder';
import UpcomingGrid from './UpcomingGrid';
import  { useState, useEffect } from 'react';
import ImageList from '@material-ui/core/ImageList'
import axios from 'axios';
import { Paper } from "@material-ui/core";
import ImageListItem from '@material-ui/core/ImageListItem';
import ImageListItemBar from '@material-ui/core/ImageListItemBar';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import { createTheme, ThemeProvider } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import { FormLabel,  FormControl , Input, InputLabel , FormHelperText , FormControlLabel} from '@material-ui/core';




export default function Home(props){

    const theme = createTheme({
        palette: {
          type: 'light',
        }
      
      });
    /*makeStyles(theme => ({
        formControl: {
         margin: theme.spacing.unit,
         minWidth: 100,
         maxWidth: 280,
         backgroundColor: theme.palette.background.light

        }
       }))*/
    
    const[imageUrls, setImageUrls] = useState([]);
    const[titles, setTitles] = useState([]);
    const[movieData, setMovieData] = useState([]);
    const[releaseDates, setreleaseDates] = useState([]);

  
    useEffect(() => {
        const getImageurls = async () => {
  
            const response = await axios.get(
            "http://localhost:8085/api/v1/movies?page=1&limit=100");
                
            const urls = response.data.movies.map((movie)=>{
                return movie.poster_url;
            });
            const titlesT = response.data.movies.map((movie)=>{
                return movie.title;
            });
           
            const releaseDatez = response.data.movies.map((movie)=>{
                return movie.release_date;
            });

            console.log(releaseDatez);
           
                setImageUrls(urls);
                setTitles(titlesT);
                setMovieData(response.data);
                setreleaseDates(releaseDatez);

        }
        getImageurls();
       
    }, []);

   
    
    return(
        <div className="home">
            <div className="header">    
                <Header/>
            </div>
            <div className="heading">
                Upcoming Movies
            </div>
            
            <div id="upcmomingMoviesGrid" className="upcoming-movies">
                <UpcomingGrid imageUrls ={imageUrls} titles = {titles} movieData={movieData}/>
            </div> 
            <div style={{  display: 'flex' }}>  
  
                <div style={{ width: "76%" , margin: "16px"}}>  
                    <Grid >
                        <ImageList rowHeight={350}  cols={4}
                        style ={{ display: 'flex' }}>
                            {imageUrls.map((item,key) => (
                                <ImageListItem key={key+499}>
                                    <img
                                    src={item}
                                    alt="Image not available"
                                    key={key+199}
                                    />
                                    <ImageListItemBar 
                                    title={titles[key]}
                                    subtitle = {`Release Date:Fri ${releaseDates[key]}`}
                                    key = {key+49}
                                    />
                                </ImageListItem>
                            ))}
                        </ImageList>     
                    </Grid> 
                </div>  

                <div style={{ width:"24%", margin: "16px"}}>  
                <ThemeProvider theme={theme}>
                <Card >
                <FormControl >
                    <FormLabel style={{ color : '#a6d4fa'}}>FIND MOVIES BY: </FormLabel>
                    
                    <InputLabel htmlFor="my-input">Movie Name</InputLabel>
                    <Input id="my-input" aria-describedby="my-helper-text" />
                    
                </FormControl>
               
                </Card> 
                </ThemeProvider>
                </div>  
            </div>  
        </div>
    )
}
