import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
import StarBorderIcon from '@material-ui/icons/StarBorder';
import Box from '@material-ui/core/Box';
import Paper from '@material-ui/core/Paper';
import ImageListItem from '@material-ui/core/ImageListItem';
import { ImageList } from '@material-ui/core';
import  { useState } from 'react';




export default function UpcomingGrid() {

    const[imageUrls, setImageUrls] = useState([]);
    
    async function getImages(){
        const rawResponse = await fetch('http://localhost:8085/api/v1/movies?page=1&limit=100',{
            method: 'GET'
        });
        if(rawResponse.ok){
            const moviesObject = await rawResponse.json();
            const movies = moviesObject.movies;
            let urls = movies.map(movie => {
                return (movie.poster_url);
            });
            setImageUrls(urls);
            console.log(imageUrls);
        }
    }

    getImages();
   

    return (
        <Box sx={{ flexGrow: 1 }}>
          <Grid container  >
            <Grid item xs={1}>
                <ImageList sx={{ width: 500, height: 450 }} cols={3} rowHeight={164}>
                   
                    {imageUrls.map((item) => (
                        <ImageListItem >
                            <img
                            src={item}
                            />

                        </ImageListItem>
                    ))}
                </ImageList>
            </Grid>            
          </Grid>
        </Box>
      );
    }
